function setCanvasWidth(){var t=document.getElementById("canvasOne");t.width=window.innerWidth,t.height=window.innerHeight,$(window).resize(function(){setCanvasWidth()})}function getRandomColor(){return"#"+((1<<24)*Math.random()|0).toString(16)}function getRandomArbitary(t,e){return Math.random()*(e-t)+t}function getMousePos(t,e){var n=t.getBoundingClientRect();return{x:e.clientX-n.left,y:e.clientY-n.top}}setCanvasWidth();var stage=new createjs.Stage("canvasOne");stage.on("stagemousedown",function(t){var e=new createjs.Shape,n=getRandomArbitary(100,400),a=getRandomArbitary(100,400);e.graphics.beginFill(getRandomColor()).drawRect(0,0,n,a),e.x=t.stageX-n/2,e.y=t.stageY-a/2,e.on("pressmove",function(e){console.log("pressmove"),t.target.x=t.stageX,t.target.y=t.stageY}),stage.addChild(e),stage.update()});